const format   = new Intl.NumberFormat('es-US', {
  style: 'currency',
  currency: 'USD',
  minimumFractionDigits:2,
  maximumFractionDigits:2,
})

function CalculateTable(props) {
  /* Todo: Show below table conditionally (only once result data is available) */
  /* Show fallback text if no data is available */

  return (
    <table className="result">
      <thead>
        <tr>
          <th>Year</th>
          <th>Total Savings</th>
          <th>Interest (Year)</th>
          <th>Total Interest</th>
          <th>Invested Capital</th>
        </tr>
      </thead>
      <tbody>
        {props.data.map((yearData) => (
          <tr key={yearData.year} >
            <td>{yearData.year}</td>
            <td>{yearData.savingsEndOfYear}</td>
            <td>{yearData.yearlyInterest}</td>
            <td>{yearData.savingsEndOfYear - props.initialInvestment - yearData.yearlyContribution * yearData.year}</td>
            <td>{props.initialInvestment + yearData.yearlyContribution * yearData.year}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
export default CalculateTable;
