import { useState } from 'react';
import classes from './UserInput.module.css';
 
const initialUserInput = {
  currSaving: 10000,
  yearlySaving: 1200,
  expectedInterest: 7,
  investDuration: 10,
};
 
const UserInput = (props) => {
  const [userInput, setUserInput] = useState(initialUserInput);
 
  const submitHandler = (event) => {
    event.preventDefault();
 
    props.onCalculate(userInput);
  };
 
  const resetHandler = () => {
    setUserInput(initialUserInput);
  };
 
  const inputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input]: +value,
      };
    });
  };
 
  return (
    <form onSubmit={submitHandler} className={classes.form}>
      <div className={classes['input-group']}>
        <p>
          <label htmlFor='current-savings'>Current Savings ($)</label>
          <input
            onChange={(event) =>
              inputChangeHandler('currSaving', event.target.value)
            }
            value={userInput['currSaving']}
            type='number'
            id='current-savings'
          />
        </p>
        <p>
          <label htmlFor='yearly-contribution'>Yearly Savings ($)</label>
          <input
            onChange={(event) =>
              inputChangeHandler('yearlySaving', event.target.value)
            }
            value={userInput['yearlySaving']}
            type='number'
            id='yearly-contribution'
          />
        </p>
      </div>
      <div className={classes['input-group']}>
        <p>
          <label htmlFor='expected-return'>
            Expected Interest (%, per year)
          </label>
          <input
            onChange={(event) =>
              inputChangeHandler('expectedInterest', event.target.value)
            }
            value={userInput['expectedInterest']}
            type='number'
            id='expected-return'
          />
        </p>
        <p>
          <label htmlFor='duration'>Investment Duration (years)</label>
          <input
            onChange={(event) =>
              inputChangeHandler('investDuration', event.target.value)
            }
            value={userInput['investDuration']}
            type='number'
            id='duration'
          />
        </p>
      </div>
      <p className={classes.actions}>
        <button
          onClick={resetHandler}
          type='reset'
          className={classes.buttonAlt}
        >
          Reset
        </button>
        <button type='submit' className={classes.button}>
          Calculate
        </button>
      </p>
    </form>
  );
};
 
export default UserInput;
